mapper.select:
select *
from schedule
where users_id = #{users_id}
    and trainer_id = #{trainer_id}
    and sdu_date = #{selectDate}

select *
from  record_data
where sdu_id = (
    select sdu_id
    from schedule
    where users_id = #{users_id}
      and trainer_id = #{trainer_id}
      and sdu_date = #{selectDate}
    )

mapper.insert:
update schedule
set sdu_com = #{SDU_COM}
where trainer_id = #{trainer_id}
    and users_id = #{users_id}
    and sdu_date = #{selectDate}
